mapper.select:
select gid,IFNULL(n,' ') n,IFNULL(p,' ') p,IFNULL(yjf,' ') yjf,IFNULL(lyqd,' ') lyqd, IFNULL(jgzl,' ') jgzl,IFNULL(zhlyjg,' ') zhlyjg,IFNULL(jgksj,' ') jgksj, IFNULL(lmzl,' ') lmzl,IFNULL(lmsyl,' ') lmsyl,IFNULL(qcfl,' ') qcfl, IFNULL(qcfzl,' ') qcfzl,IFNULL(year,' ') year from t_agriinputinfo where 1=1
and year between #{syear} and #{eyear}
order by year desc limit #{currIndex},#{limit}

mapper.update:
update t_agriinputinfo set n=#{n},p=#{p},yjf=#{yjf},lyqd=#{lyqd},
jgzl=#{jgzl},zhlyjg=#{zhlyjg},jgksj=#{jgksj},lmzl=#{lmzl},
lmsyl=#{lmsyl},qcfl=#{qcfl},qcfzl=#{qcfzl},year=#{year}
 where gid=#{gid}
