mapper.select:
select gid, city, station_name, county, towns, village, date_format(quality_time, '%Y-%m-%d') quality_time, so2, no2, co, o3_8h, pm10, pm2_5, preparer, auditor, date_format(paper_time, '%Y-%m-%d') paper_time from t_air_quality
where city like #{city}
where quality_time = #{quality_time}
and quality_time = #{quality_time}
order by quality_time desc limit #{currIndex},#{limit}

    select count(gid)
