mapper.select:
        select gid,
			   quality_address,
			   county,
			   date_format(quality_time, '%Y-%m-%d') quality_time,
			   quality_type,
			   quality_result,
			   remarks
          from t_water_quality 
    	
    		
        	where quality_address like #{quality_address} 
        
        
        	
        		where quality_time = #{quality_time}
        	
        
        
        	and quality_time = #{quality_time}
       	
       	 order by quality_time desc limit #{currIndex},#{limit}

		select gid
  	  from t_water_quality
  	 where county = #{county}
  	   and quality_time = #{quality_time}
  	   and quality_type = #{quality_type}

mapper.update:
update t_water_quality
   set quality_result = #{quality_result},
   	   remarks = #{remarks},
   	   modifier = #{modifier}
 where gid = #{gid}
