mapper.select:
select gid, date_year, county, towns, plant_product_count, animal_product_count, fish_product_count, high_quality_plant_count, high_quality_animal_count, high_quality_fish_count from t_farmproduct_quality where 1=1
and date_year >= #{date_year}
and date_year <= #{date_year1}
order by date_year desc, gid limit #{currIndex},#{limit}

 select count(gid)
   from t_farmproduct_quality 
	 where 1=1
	
 	and date_year >= #{date_year}
	
	
 	and date_year <= #{date_year1}

		select gid
  	  from t_farmproduct_quality
