mapper.select:
select gid, date_year, county, towns, village, code_number, organic, nitrogen, phosphorus, effective_phosphorus, potassium, ph, salinity, available_potassium, slow_release_potassium from t_soil_quality
where date_year like #{date_year}
where code_number = #{code_number}
and code_number = #{code_number}
order by date_year desc limit #{currIndex},#{limit}

		select gid
  	  from t_soil_quality
  	 where date_year = #{date_year}
  	   and code_number = #{code_number}
