mapper.select:
        select a.gid,a.operation_type,a.operation_status,a.operation_log,a.create_time,
        CONCAT_WS('-',a.creator,b.user_showname) user_showname 
        from t_system_log a left join t_user_info b on a.operation_user=b.user_id
        
	        and a.operation_type=#{operation_type}
	    
	    
	        and a.operation_status=#{operation_status}
	    
	    order by a.gid desc limit #{currIndex},#{limit}

        select count(a.gid) from t_system_log a left join t_user_info b on a.operation_user=b.user_id
        
	        and a.operation_type=#{operation_type}
	    
	    
	        and a.operation_status=#{operation_status}
