mapper.select:
select gid,IFNULL(n,' ') n,IFNULL(p,' ') p,IFNULL(yjf,' ') yjf,IFNULL(lyqd,' ') lyqd, IFNULL(jgzl,' ') jgzl,IFNULL(zhlyjg,' ') zhlyjg,IFNULL(jgksj,' ') jgksj, IFNULL(lmzl,' ') lmzl,IFNULL(lmsyl,' ') lmsyl,IFNULL(qcfl,' ') qcfl, IFNULL(qcfzl,' ') qcfzl,IFNULL(year,' ') year, IFNULL(k,' ') k,IFNULL(fhf,' ') fhf ,IFNULL(ljf,' ') ljf,IFNULL(dm_syl,' ') dm_syl,IFNULL(dm_fgmj,' ') dm_fgmj,IFNULL(lysyl,' ') lysyl ,IFNULL(lycyl,' ') lycyl,IFNULL(bz,' ') bz from t_agriinputinfo where 1=1
and year between #{syear} and #{eyear}
order by year desc limit #{currIndex},#{limit}
