mapper.select:
        select a.gid,a.county,a.farm_name,a.farm_address,a.legal_person,a.phone_num,b.type_name animals_name,a.animals_size,a.remarks 
        from t_farm_info a,t_animals_type b where a.animals_type=b.gid
        
        	
	        and a.farm_name like #{farm_name}
	    
	    
	    	and b.gid=#{type}
	    
	    order by a.create_time desc
