mapper.select:
select gid, date_year, county, towns, grass_area, grass_usable_area, grass_retain_area, grass_unforage, grass_animal_balance, plateau_pika_area, plateau_zokor_area, grass_worm_area, grasshopper_area from t_grass_quality
where date_year = #{date_year}
order by date_year desc limit #{currIndex},#{limit}

        select count(gid)
          from t_grass_quality 
       	
			where date_year = #{date_year}

		select gid
  	  from t_grass_quality
  	 where county = #{county}
  	   and date_year = #{date_year}

select ${grassIndex},
	   towns,
	   date_year
  from t_grass_quality 
 
 	where towns = #{towns}
 
 
 	where date_year = #{date_year}
 
 
 	and towns = #{towns}
 
 
 	and date_year = #{date_year}
 
 order by date_year desc limit 0,10
