mapper.select:
		select quality_type
		  from t_water_quality
	  	 where 1=1
    	
    		
        	and quality_address like #{quality_address} 
        
        
        	and quality_time >= #{quality_time}
       	
       	
        	and quality_time <= #{quality_time1}
       	
       	 group by quality_type
       	 order by quality_type

select gid, quality_address, county, towns, date_format(quality_time, '%Y-%m-%d') quality_time, quality_type, quality_result, remarks from t_water_quality where 1=1
and quality_address like #{quality_address}
and quality_time >= #{quality_time}
and quality_time <= #{quality_time1}
order by quality_time desc, gid limit #{currIndex},#{limit}

    select count(gid)
      from t_water_quality 
	 where 1=1
	
		
    	and quality_address like #{quality_address} 
    
    
    	and quality_time >= #{quality_time}
   	
   	
    	and quality_time <= #{quality_time1}

		select gid
  	  from t_water_quality
